import Home from './components/Home.jsx';
import Airport from './components/Airport.jsx'
import Login from './components/Login.tsx'
import User from './components/User.tsx';
import env from 'dotenv';
import { useGoogleLogin } from 'react-google-login';
import { Children, useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';

function App() {
  const [isLoggedIn, setLoginStatus] = useState(false);
  const [userName, setUserName] = useState<any | null>(null);
  const [userEmail, setUserEmail] = useState<any | null>(null);
  const onLoginSuccess = (res) => {
      if(res.profileObj) {
          setLoginStatus(true);
          setUserName(res.profileObj.name);
          setUserEmail(res.profileObj.email);
      } else { setLoginStatus(false); }
      console.log(res.profileObj);
  };

  const onLoginFailure = (res) => {
      console.log(res);
  };

  const { signIn, loaded } = useGoogleLogin({
      onSuccess: onLoginSuccess,
      onFailure: onLoginFailure,
      isSignedIn: true,
      clientId: env("google_clientId")
  });
  return (
      <Routes>
          {isLoggedIn ? (
              <><Route path="/" element={<Home />} /><Route path="/user" element={<User fullName={userName} email={userEmail}/>} /><Route path="/:airport" element={<Airport />} /></>
          ) : (
              <Route path="/login" element={<Login />} />
          )}
      </Routes>
  );
}

export default App;
