import Home from './components/Home/Home.tsx';
import Metar from './components/Airport/Metar.tsx'
import Login from './components/Login/Login.tsx'
import User from './components/User/User.tsx';
import axios from "axios";
import { useGoogleLogin } from 'react-google-login';
import { useEffect, useState } from 'react';
import {Routes, Route, Navigate } from 'react-router-dom';
import env from "dotenv";
import './App.css';
import Airport from './components/Airport/Airport.tsx';
import "./spinner.css";


function App() {
  let [isLoading, setLoadingStatus] = useState(true);
  let [firstName, setFirstName] = useState(null);
  let [lastName, setLastName] = useState(null);
  let [email, setEmail] = useState(null);
  let [profile_picture, set_profile_picture] = useState(null);
  let [errorCodeStatus, setErrorCodeStatus] = useState(null);
  useEffect(() => {
    axios.get("http://localhost:4000/auth/user/information", {withCredentials: true}).then((res) => {
      if(res.data.name === undefined) setErrorCodeStatus(true); else setErrorCodeStatus(false);
      setFirstName(res.data.name.familyName);
      setLastName(res.data.name.givenName);
      setEmail(res.data.emails.main);
      set_profile_picture(res.data.profile_picture.value);
  }).catch((err) => { console.log(err) })
  setTimeout(() => { setLoadingStatus(false) }, 750)
  }, [])

  return (
      <Routes>
            <>
              {isLoading ? (
                  <div className="lds-ring">
                  <div></div><div></div><div></div><div></div>
                  </div>
              ) : (
                <div>
                  {errorCodeStatus ? (
                    <Navigate to="/login"/>
                  ) : (
                    <><Route path="/" element={<Home />} /><Route path="/user" element={<User isSignedIn={errorCodeStatus} firstName={firstName} lastName={lastName} email={email} profilePicture={profile_picture}/>} /><Route path="/metar/:airport" element={<Metar />} /><Route path="/:airport" element={<Airport />} /></>
                  )}
                </div>
              )}
                <Route path="/login" element={<Login />} />
            </>
      </Routes>
  );
}

export default App;
