import React, {useEffect, useState} from "react";
import axios from 'axios';
import { useParams } from 'react-router-dom';
let params = useParams();
let [icao_code, set_icao_code] = useState(null);
let [airportName, setAirportName] = useState(null);
let [airportLocation, setAirportLocation] = useState(null);
let [airport_raw_text, set_airport_raw_text] = useState(null);
let [airport_temperature_in_celsius, set_airport_temperature_in_celsius] = useState(null);
let [airport_temperature_in_fahrenheit, set_airport_temperature_in_fahrenheit] = useState(null);

async function getMetarInformation() {
    useEffect(() => {
        // GET request using axios inside useEffect React hook
        axios.get(`http://localhost:4000/metar?icao=${params.airport}`)
            .then(response => {
                set_icao_code(response.data.icao)
                setAirportName(response.data.station.name)
                setAirportLocation(response.data.station.location)
                set_airport_raw_text(response.data.raw_text);
                set_airport_temperature_in_celsius(response.data.temperature.celsius);
                set_airport_temperature_in_fahrenheit(response.data.temperature.fahrenheit);
            })
            .catch(err => { console.log(err); });
    // empty dependency array means this effect will only run once (like componentDidMount in classes)
    }, []);
}
function Airport() { 
    return (
        <>
          <main>
              <div>
                  <h1>{icao_code}</h1>
                  <div>
                    <h2>{airportName}</h2>
                    <h3>{airportLocation}</h3>
                    </div>
                  <div>
                      <div>
                        <h3>METAR - Current</h3>
                      </div>
                      <div>
                          <table className="table">
                              <tbody>
                                  <tr>
                                      <th colSpan="2">
                                          <p>{airport_raw_text}</p>
                                      </th>
                                  </tr>
                                  <tr>
                                      <th>Flight Category</th>
                                      <td>
                                          {}
                                      </td>
                                  </tr>
                                  <tr>
                                      <th>Temperature</th>
                                      <td>
                                          {airport_temperature_in_celsius}° C / {airport_temperature_in_fahrenheit}° F
                                      </td>
                                  </tr>
                              </tbody>
                          </table>
                      </div>
                  </div>
              </div>
          </main>
        </>
    );
}


export default Airport;
