import React, {useEffect, useState} from "react";
import axios from 'axios';
import lodash from 'lodash';
import { useParams, Link } from 'react-router-dom';
import './Metar.css';

function Metar() {
    let params = useParams();
    let padded_wind_direction
    let [icao_code, set_icao_code] = useState(params.airport);
    fetchData(async () => {
        try {
            let res = await axios.get(`http://localhost:4000/metar?icao=${icao_code}`);
            let [airportName, setAirportName] = useState(res.data.station.name);
            let [airportLocation, setAirportLocation] = useState(res.data.station.location);
            let [airport_raw_text, set_airport_raw_text] = useState(res.data.raw_text);
            let [airport_temperature_in_celsius, set_airport_temperature_in_celsius] = useState(res.data.temperature.celsius);
            let [airport_temperature_in_fahrenheit, set_airport_temperature_in_fahrenheit] = useState(res.data.temperature.fahrenheit);
            let [flight_category, set_flight_category] = useState(null);
            let [wind_direction, set_wind_direction] = useState(null);
            let [wind_speed, set_wind_speed] = useState(null);
        } catch (err) { console.log(err); }
    });

    useEffect(() => {
        // GET request using axios inside useEffect React hook
        axios.get(`http://localhost:4000/metar?icao=${icao_code}`)
            .then(response => {
                set_icao_code(response.data.icao)
                setAirportName(response.data.station.name)
                setAirportLocation(response.data.station.location)
                set_airport_raw_text(response.data.raw_text);
                set_flight_category(response.data.flight_category);
                if(response.data.temperature.celsius === undefined && response.data.temperature.fahrenheit === undefined) { 
                    set_airport_temperature_in_celsius(response.data.temperature.minimum.celsius);
                    set_airport_temperature_in_fahrenheit(response.data.temperature.minimum.fahrenheit);
                 } else { 
                        set_airport_temperature_in_celsius(response.data.temperature.celsius); 
                        set_airport_temperature_in_fahrenheit(response.data.temperature.fahrenheit); 
                    }
                if(response.data.wind.degrees >= 100) { set_wind_direction(response.data.wind.degrees); } else {
                    padded_wind_direction = response.data.wind.degrees.toString();
                    set_wind_direction(padded_wind_direction.padStart(3, '0'));
                }
                set_wind_speed(response.data.wind.speed_kts);
            })
            .catch(err => { console.log(err); });
    // empty dependency array means this effect will only run once (like componentDidMount in classes)
    }, [wind_direction]);
    return (
        <>
          <main>
              {lodash.isEmpty(icao_code) ? (
                  <h1>That airport does not exist</h1>
              ) : (

                <div>
                    <div>
                        <Link to={`/${icao_code}`}>{icao_code}</Link><br></br>
                        <Link to={`/metar/${icao_code}`} onClick={(() => window.location.reload())}>Metar</Link>
                    </div>
                <h1>{icao_code}</h1>
                <div>
                  <h2>{airportName}</h2>
                  <h3>{airportLocation}</h3>
                  </div>
                <div>
                    <div className="metarTitle">
                      <h3>METAR - Current</h3>
                    </div>
                    <div className="metarTable">
                        <table className="table">
                            <tbody>
                                <tr>
                                    <th colSpan={2}>
                                        <p>{airport_raw_text}</p>
                                    </th>
                                </tr>
                                <tr>
                                    <th>Flight Category</th>
                                    <td>
                                    <span className="badge text-white">
                                      {flight_category}
                                    </span>
                                    </td>
                                </tr>
                                <tr>
                                    <th>Wind</th>
                                    <td>
                                        {wind_direction}° at {wind_speed} KTS
                                    </td>
                                </tr>
                                <tr>
                                    <th>Temperature</th>
                                    <td>
                                        {airport_temperature_in_celsius}° C / {airport_temperature_in_fahrenheit}° F
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
          )}
          </main>
        </>
    );
}


async function fetchData(icao_code) {
    try {
        let res = await axios.get(`http://localhost:4000/metar?icao=${icao_code}`);
        return await res.data;
    } catch (err) { console.log(err); }
}

export default Metar;
