import React, { useState } from "react";
import env from "react-dotenv";
import { useGoogleLogin } from "react-google-login"; 

const handleLocationSubmission = (e) => {
    e.preventDefault();
    window.location = `/${e.target[0].value.toUpperCase()}`
};


function Home(props) {
    const [isLoggedIn, setLoginStatus] = useState(false);
    const onLoginSuccess = (res) => {
        if(res.profileObj) {
            setLoginStatus(true);
        } else { setLoginStatus(false); }
        console.log(res.profileObj);
    };

    const onLoginFailure = (res) => {
        console.log(res);
    };
    const { signIn, loaded } = useGoogleLogin({
        onSuccess: onLoginSuccess,
        onFailure: onLoginFailure,
        isSignedIn: true,
        clientId: "817352508433-svhmh1kd4bg80l1jb10g9kq91qkvslv6.apps.googleusercontent.com"
    });
    const signInPage = window.location = "http://localhost:3000/login";
    return (
        <div>
            <div>
            {isLoggedIn ? (
                        <form onSubmit={handleLocationSubmission}>
                            <label label="location">Enter ICAO Code</label>
                            <input type="text" name="location"></input>
                            <input type="submit" value="Submit"></input>
                        </form>
            ) : (
                {signInPage}
            )}
            </div>
        </div>
    );
}
export default Home;
