import React, {useEffect, useState} from "react";
import axios from 'axios';
import { useParams } from 'react-router-dom';


function Airport() {
    let params = useParams();
    const [icao_code, set_icao_code] = useState(null);
    const [airportName, setAirportName] = useState(null);
    const [airportLocation, setAirportLocation] = useState(null);
    let [airport_raw_text, set_airport_raw_text] = useState(null);

    useEffect(() => {
        // GET request using axios inside useEffect React hook
        axios.get(`http://localhost:4000/metar?icao=${params.airport}`)
            .then(response => {
                set_icao_code(response.data.icao)
                setAirportName(response.data.name)
                setAirportLocation(response.data.location)
            })
            .catch(err => { console.log(err); });
    // empty dependency array means this effect will only run once (like componentDidMount in classes)
    }, []); 
    return (
        <h1>{icao_code}</h1>
    );
}


export default Airport;
